mapper.select:
select top (convert(int,#{rows})) temp.* from ( select row_number() over (order by t.create_time desc) as RowNumber, t.*,case gf.name when '1' then '网络' when '2' then '服务器' when '3' then '监控' when '4' then '虚拟化' when '5' then '云' when '6' then '视频' end name,gf.name fieldid,ad.pro_name province,ad.city_name city,ad.dis_name district,ad.details address,tsk.cnt from app_task t left join app_address ad on t.address_id=ad.id left join app_good_field gf on t.field_id=gf.id left join ( select tu.order_id,tu.user_id,count(*) cnt from app_task_user tu group by tu.order_id,tu.user_id )tsk on tsk.order_id=t.id
left join app_user_info us on tsk.user_id=us.id
left join app_enterprises_info us on tsk.user_id=us.id
where 1=1
and tsk.user_id=#{user_id}
and us.token=#{token}
and t.id=#{task_id}
and t.title=#{title}
) as temp where RowNumber > convert(int,#{rows})*(convert(int,#{page})-1) order by temp.create_time desc

mapper.update:
update app_task_user set
status=#{status},
update_time=getdate() where order_id = #{task_id}

mapper.select:
select t.*, case g.name when '1' then '网络' when '2' then '服务器' when '3' then '监控' when '4' then '虚拟化' when '5' then '云' when '6' then '视频' end field,i.id user_id,i.name user_name,i.icon_url url,i.auth_create_time auth_time from app_task t left join app_enterprises_info f on t.create_user_id=f.id left join app_good_field g on t.field_id=g.id left join app_user_field uf on g.id=uf.field_id left join app_user_info i on uf.user_id=i.id where 1=1
and t.create_user_id=#{e_id}
and t.id=#{task_id}
and f.token=#{token}
